import React, { useContext, useEffect, useState } from 'react';
import { ProductContext } from '../Data/ProductContext';
import { useParams } from 'react-router-dom';
import Product from '../Components/Product/Product.jsx';

const ProductDetail = ({ product}) => {

    const {allProducts, filteredProducts} = useContext(ProductContext)


    const { getProductsByID } = useContext(ProductContext);
    const [productDetail, setProductDetail] = useState({});
    const { id } = useParams();

    const fetchProducts = async (id) => {
        const data = await getProductsByID(id);
        setProductDetail(data);
        };

    useEffect(() => {
        fetchProducts(id);
    }, [getProductsByID]);


    return (
        <div>
            hola mundo 2


            <div className="product-details">
                <div className='product-img'>
                <img src={productDetail.img} alt={productDetail.name} />
                </div>
                <div className="product-info">
                <h2>{productDetail.name}</h2>
                <p className="product-price">{productDetail.price}</p>
                
                <div className='descriptions'>
                    <h5>{productDetail.description}</h5>
                </div>
                </div>
            </div>


             <div className="card-container">
            
            
                {/* <>
                {allProducts.map((product) => (
                    <Product
                    key={product.id}
                    product={product}
                    />
                ))}
                </> */}
             
             {getProductsByID.map((product) => {

                return(
                    <p>{product.name} </p>
                )
             }
             
             )}
        
        </div>


        </div>
    )
}

export default ProductDetail;
